import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons';
import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
width: 100%;
height: 100vh;
display: flex;
position: relative;
overflow:hidden;
`;
const Arrow = styled.div`
width: 50px;
height: 50px;
background-color: #fff7f7;
border-radius: 50%;
display:flex;
align-items: center;
justify-content: center;
position: absolute;
top: 0;
bottom :0;
left:${props=>props.direction ==="left"&& "10px"};
right:${props=>props.direction==="right"&& "10px"};

margin: auto;
cursor:pointer;
opacity:0.5;

`;
const Wrapper = styled.div`
height:100%;
display: flex;
transform: translateX(-100vw);
`;
const Slide =styled.div`
width:100vw;
height: 100vh;
display: flex;
align-items: center;
background-color:#${props=> props.bg};
`;
const ImgContainer =styled.div`
height :100%;
flex:1;

`;
const Image = styled.img`
   height:80%;
`;
const InfoContainer =styled.div`
flex:1;
padding:50px;
`;
const Title = styled.h1`
font-size: 70px`;
const Desc = styled.p`
margin: 50px 0px;
font-size: 20px;
font-weight: 500;
letter-spacing: 3px;
`;
const Button = styled.button`
padding:10px;
font-size:20px;
background-color: transparent;
cursor:pointer;`;
const Slider = () => {
    const handleClick=(direction)=>{}
  return (
  <Container>
  <Arrow direction="left" onClick={()=>handleClick("left")}>
    <ArrowLeftOutlined/>
  </Arrow>
  <Wrapper>
  <Slide bg="fcf1ed">
    <ImgContainer>
        <Image src="https://media.istockphoto.com/photos/studio-photo-of-different-fruits-and-vegetables-picture-id518504426?k=20&m=518504426&s=612x612&w=0&h=GrY1f6bIzoblsdd7vKLat5bWzvHFjgVS4Ezwwu-sIbo="/>
    </ImgContainer>
    <InfoContainer>
        <Title>GET DOORSTEP DELIVERY</Title>
        <Desc> Get your daily needs delivered superfast </Desc>
        <Button>SHOP NOW</Button>
    </InfoContainer>
    </Slide>
    <Slide>
    <ImgContainer>
        <Image src="https://images.unsplash.com/photo-1604776485611-4371f7a48cc2?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8M3x8Z2VuZXJhbCUyMHN0b3JlfGVufDB8fDB8fA%3D%3D&w=1000&q=80"/>
    </ImgContainer>
    <InfoContainer>
        <Title>general store</Title>
        <Desc> one stop for all your cosmetics and essentials </Desc>
        <Button>SHOP NOW</Button>
    </InfoContainer>
    </Slide>
    <Slide>
    <ImgContainer>
        <Image src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/52/Carrefour_Market_Voisins-le-Bretonneux_2012_07.jpg/800px-Carrefour_Market_Voisins-le-Bretonneux_2012_07.jpg"/>
    </ImgContainer>
    <InfoContainer>
        <Title>grocery</Title>
        <Desc> Fresh vegitables and fruits </Desc>
        <Button>SHOP NOW</Button>
    </InfoContainer>
    </Slide>
  </Wrapper>
  <Arrow direction="right"onClick={()=>handleClick("left")}>
    <ArrowRightOutlined/>
  </Arrow>
  </Container>
  )
}

export default Slider
